using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using Xunit;

namespace RNEmergency.Model
{
    public class PetitionResultTests
    {
        public class sign_image_dataurlProperty
        {
            [Fact]
            public void SetDataUrl_sign_image_and_bytes_are_set()
            {
                var target = new PetitionResult();
                target.sign_image_dataurl = @"data:image/png;base64,

";
                Assert.NotEmpty(target.sign_image_bytes);
                Assert.NotEmpty(target.sign_image);
            }

            [Fact]
            public void SetDataUrl_with_no_data_tag()
            {
                var target = new PetitionResult();
                target.sign_image_dataurl = @"

";
                Assert.NotEmpty(target.sign_image_bytes);
                Assert.NotEmpty(target.sign_image);
            }
        }
    }
}
